import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import {
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE,
} from "../../shared/util/validators";
import Card from "../../shared/components/UIElements/jsx/Card";
import { useForm } from "../../shared/hooks/form-hook";
import "./PlaceForm.scss";

const DUMMY_PLACES = [
  {
    id: "p1",
    imageUrl:
      "https://static01.nyt.com/images/2019/09/22/us/22TRUMPTOWER/22TRUMPTOWER-videoSixteenByNineJumbo1600.jpg",
    title: "Trump Tower",
    description: "Trump Tower in New York",
    address: "725 5th Ave, New York, NY 10022",
    creatorId: "u1",
    location: {
      lat: 40.7624,
      lng: -73.9738,
    },
  },
  {
    id: "p2",
    imageUrl:
      "data:image/jpeg;base64,",
    title: "South Pole",
    description: "Antarctica",
    address: "None",
    creatorId: "u1",
    location: {
      lat: -84.9999724,
      lng: 44.999447,
    },
  },
];

const UpdatePlace = () => {
  const [isLoading, setIsLoading] = useState(true);

  const placeId = useParams().placeId;

  const [formState, inputHandler, setFormData, defaultSubmit] = useForm(
    {
      title: {
        value: "",
        isValid: false,
      },
      description: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const identifiedPlace = DUMMY_PLACES.find((p) => p.id === placeId);
  useEffect(() => {
    if (identifiedPlace) {
      setFormData(
        {
          title: {
            value: identifiedPlace.title,
            isValid: true,
          },
          description: {
            value: identifiedPlace.description,
            isValid: true,
          },
        },
        true
      );
    }
    setIsLoading(false);
  }, [setFormData, identifiedPlace]);

//   const placeUpdateSubmitHandler = (event) => {
//     event.preventDefault();
//     console.log(formState.inputs);
//   };

  if (!identifiedPlace) {
    return (
      <div className="center">
          <Card>
        <h2>Could not find place.</h2>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="center">
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <form className="place-form" onSubmit={defaultSubmit}>
      <Input
        id="title"
        element="input"
        type="text"
        label="Title"
        validators={[VALIDATOR_REQUIRE()]}
        onInput={inputHandler}
        value={formState.inputs.title.value}
        valid={formState.inputs.title.isValid}
      ></Input>
      <Input
        id="description"
        element="textarea"
        label="Description"
        validators={[VALIDATOR_MINLENGTH(5)]}
        onInput={inputHandler}
        value={formState.inputs.description.value}
        valid={formState.inputs.description.isValid}
      ></Input>
      <Button type="submit" disabled={!formState.isValid}>
        Update Place
      </Button>
    </form>
  );
};

export default UpdatePlace;
